/*--------------------------------------------------------------------
 CELL NAME : drive_analog_input
---------------------------------------------------------------------*/

`timescale 1 ps/1 ps

module drive_analog_input ( parallel_in, serial_out );

  input  [63:0]  parallel_in;
  output         serial_out;

  reg            serial_out=1'bz;
  integer        i;

  always @ ( parallel_in )
  begin
    #0;
    for ( i=0; i<=63; i=i+1 ) begin
      serial_out = parallel_in[i];
      #0;
      serial_out = 1'bx;
      #0;
      if ( i == 63 )
        serial_out = 1'bz;
    end
  end

endmodule
