  input          serial_in;
  input          read_enb;
  output [63:0]  parallel_out;

  reg    [63:0]  parallel_out;
  integer        i=0;
  
  always @ ( serial_in )
  begin
    if ( read_enb == 1'b0 ) begin
      if ( serial_in !== 1'bz && serial_in !== 1'bx && i < 64 ) begin
