/*--------------------------------------------------------------------
 CELL NAME : CLKDLY 
---------------------------------------------------------------------*/

`timescale 1 ps/1 ps

module CLKDLY (
  CLK,
  DLYGL0,
  DLYGL1,
  DLYGL2,
  DLYGL3,
  DLYGL4,
  GL
  );

  output GL;
  input  CLK; 
  input  DLYGL0;
  input  DLYGL1;
  input  DLYGL2;
  input  DLYGL3;
  input  DLYGL4;

  parameter INTRINSIC_DELAY     = 528;
  parameter PROG_INIT_DELAY     = 535;
  parameter PROG_STEP_INCREMENT = 200;

  time       GLDELAY;

  wire [4:0] DLYGL_ipd;
  wire       CLK_ipd;

  reg        CLK_ipd_delayed;

  reg        dlygl_xor;
  integer    dlygl_step;

  buf U0 ( CLK_ipd, CLK );

  assign DLYGL_ipd[0]  = DLYGL0;
  assign DLYGL_ipd[1]  = DLYGL1;
  assign DLYGL_ipd[2]  = DLYGL2;
  assign DLYGL_ipd[3]  = DLYGL3;
  assign DLYGL_ipd[4]  = DLYGL4;

  buf BUF_DLY_0( GL, CLK_ipd_delayed );

  always @ ( DLYGL_ipd )
  begin
    dlygl_xor = ^ DLYGL_ipd;
    if ( dlygl_xor === 1'bX )
    begin
      GLDELAY <= 0;
    end else
    begin
      dlygl_step = DLYGL_ipd;
      GLDELAY <= ( dlygl_step == 0 ) ?
                 INTRINSIC_DELAY :
                 ( INTRINSIC_DELAY + ( dlygl_step * PROG_STEP_INCREMENT ) + PROG_INIT_DELAY );
    end
  end

  always @( GLDELAY or CLK_ipd)
  begin
     CLK_ipd_delayed <= #(GLDELAY) CLK_ipd;
  end

  initial
  begin
    GLDELAY = 0;
  end
  
endmodule
//---- END MODULE CLKDLY ----

