  // ---------
  // RTC reset
  // ---------

  always @ ( rtm_rst ) 
  begin
    if ( rtm_rst == 1'b1 ) begin
      for ( q = 63; q < 84; q = q+1) begin
        MEMORY[q] = 8'b0;
      end
      //prescaler_xtl_count <= 0;
      //rtc_counter_bits    <= 40'b0;
      rtc_match_reg_bits  <= 40'b0;
      capture_reg         <= 40'b0;
    end
    else if ( rtm_rst === 1'bx ) begin
      $display ( " Warning : rtm_rst bit in control status register is unknown " );
      $display ( " Time: %0.1f Instance: %m ", $realtime );
    end
