  // Prescaler clock generation logic

  always @ ( posedge RTCCLK_ipd or posedge rtm_rst or negedge cntr_en )
  begin
    // rtm_rst active(1'b1) or cntr_en in-active(1'b0) resets pre-scaler count and output
    if ( rtm_rst == 1'b1 || cntr_en == 1'b0 ) begin
      prescaler_clk       <= 1'b0;
      prescaler_xtl_count  =  'd0;
    end
    else if ( sync_enb == 1'b0 && ( RTCCLK_ipd == 1'b1 && RTCCLK_ipd_last == 1'b0 ) ) begin
      prescaler_xtl_count = prescaler_xtl_count + 1;
      if ( prescaler_xtl_count == 64 ) begin
        prescaler_clk <= 1'b1;
      end
      else if ( prescaler_xtl_count % 64 == 0) begin
        prescaler_clk <= !prescaler_clk;
      end
    end
  end // always @ ( posedge RTCCLK_ipd or posedge rtm_rst or negedge cntr_en ) 

  // RTC Counter logic

  always @ ( posedge prescaler_clk or posedge rtm_rst or negedge rstb_cnt )
  begin
    // rtm_rst active(1'b1) or rstb_cnt active (=1'b0) resets rtc counter
    if ( rtm_rst == 1'b1 || rstb_cnt == 1'b0 ) begin
      rtc_counter_bits = 40'b0;
    end
    else if ( sync_enb == 1'b0 && ( prescaler_clk == 1'b1 ) ) begin
      //if ( rtm_rst == 1'b0 && cntr_en == 1'b1 ) begin
      if ( RTCMATCH && rst_cnt_on_match ) begin
        rtc_counter_bits = 40'b0;
      end
      else if ( rstb_cnt ) begin
        // rtc counter increment
