  $setup ( posedge ACMWDATA[0], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $setup ( negedge ACMWDATA[0], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, posedge ACMWDATA[0], 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, negedge ACMWDATA[0], 0.0, NOTIFY_REG );
  $setup ( posedge ACMWDATA[1], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $setup ( negedge ACMWDATA[1], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, posedge ACMWDATA[1], 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, negedge ACMWDATA[1], 0.0, NOTIFY_REG );
  $setup ( posedge ACMWDATA[2], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $setup ( negedge ACMWDATA[2], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, posedge ACMWDATA[2], 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, negedge ACMWDATA[2], 0.0, NOTIFY_REG );
  $setup ( posedge ACMWDATA[3], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $setup ( negedge ACMWDATA[3], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, posedge ACMWDATA[3], 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, negedge ACMWDATA[3], 0.0, NOTIFY_REG );
  $setup ( posedge ACMWDATA[4], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $setup ( negedge ACMWDATA[4], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, posedge ACMWDATA[4], 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, negedge ACMWDATA[4], 0.0, NOTIFY_REG );
  $setup ( posedge ACMWDATA[5], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $setup ( negedge ACMWDATA[5], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, posedge ACMWDATA[5], 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, negedge ACMWDATA[5], 0.0, NOTIFY_REG );
  $setup ( posedge ACMWDATA[6], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $setup ( negedge ACMWDATA[6], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, posedge ACMWDATA[6], 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, negedge ACMWDATA[6], 0.0, NOTIFY_REG );
  $setup ( posedge ACMWDATA[7], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $setup ( negedge ACMWDATA[7], posedge ACMCLK &&& ACMWEN, 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, posedge ACMWDATA[7], 0.0, NOTIFY_REG );
  $hold  ( posedge ACMCLK &&& ACMWEN, negedge ACMWDATA[7], 0.0, NOTIFY_REG );

  // checking timing for control signals

  // hold and recovery timing checks for ADCRESET, SYSCLK

  $recovery ( negedge ADCRESET, posedge SYSCLK,   0.0, NOTIFY_REG );
  $hold     ( posedge SYSCLK,   negedge ADCRESET, 0.0, NOTIFY_REG );

  // hold and recovery timing checks for ACMRESET, ACMCLK

  $recovery ( posedge ACMRESET, posedge ACMCLK,   0.0, NOTIFY_REG );
  $hold     ( posedge ACMCLK,   posedge ACMRESET, 0.0, NOTIFY_REG );

  // period timing check on ACMCLK, RTCCLK and SYSCLK

  $period( posedge ACMCLK, 100, NOTIFY_REG );
  $period( negedge ACMCLK, 100, NOTIFY_REG );

  $period( posedge RTCCLK,  50, NOTIFY_REG );
  $period( negedge RTCCLK,  50, NOTIFY_REG );

