					endcase
          `DATA_A_OUT = drive_data_x (ADDRB, ADDRA, {WIDTHB1,WIDTHB0}, {WIDTHA1,WIDTHA0}, `DATA_A_OUT);
				end
      end
			

      case (`BLKB_WIDTH_CFG)
            2'b00 : begin

                 MEM_512_9[ ADDRB[11:3] ] [ ADDRB[2:0] ] = DINB0_reg;

                 if (WMODEB_int == 1'b1) begin
                  if (PIPEB_int == 1'b0) begin
